import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";

import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Switch from '@material-ui/core/Switch';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Toolbar from '@material-ui/core/Toolbar';

class UserSettings extends Component {
    constructor(props) {
        super(props)
        this.state ={
          value:''
        }

    }

    render() {

        const styles = {
            fabStyle: {
              color: 'white',
              backgroundColor: 'rgb(54, 193, 54)',
              position: 'fixed',
              display: 'block',
              bottom: '20px',
              right: '20px'
            },
            username: {
              marginLeft: '1em'
            },
            grow: {
              flexGrow: 1
            },
            grow2: {
              flexGrow: 2
            },
            displayOption:{
              marginLeft: '1em',
              marginRight: '1em'
            }
          }

        return(
            <div>
                <AppBar position="static" >
                    <Toolbar className="green">
                        <Button  onClick={this.props.handleLogout}><Link to="/">LOG OUT</Link></Button>
                        <Button><Link to="/inner">My Job Tickets</Link></Button>                                 
                        <div style={styles.grow}/>             
                        <h1 className="green userview-logo">APPLi</h1>         
                    </Toolbar>
                </AppBar>
                <Card>

                </Card>


            </div>
        )
    }

}

export default UserSettings
