import React, { Component } from 'react';

import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Switch from '@material-ui/core/Switch';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Toolbar from '@material-ui/core/Toolbar';

class ChangePassword extends Component {
    constructor(props) {
        super(props)
        this.state={
            oldPassword: '',
            newPassword: ''
        }
        this.changePassword = this.changePassword.bind(this)
    }

    render() {

        return(
            <div>
                <p>Change the Password</p>
                <form onSubmit={this.updateTicket}>
                    <FormControl margin="normal">
                        <InputLabel htmlFor="component-simple">Current Password</InputLabel>
                        <Input id="component-simple" autoComplete='off' value={this.state.name}  onChange={this.handleChange('name')} />
                    </FormControl>
                </form>
            </div>

        ) 
    }
}

export default ChangePassword
