import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";

import UserStyle from './UserStyle.js'
import ChangePassword from './ChangePassword'

import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Switch from '@material-ui/core/Switch';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Toolbar from '@material-ui/core/Toolbar';

class UserSettings extends Component {
    constructor(props) {
        super(props)
        this.state ={
          value: 0,
        }
        this.handleTab = this. handleTab.bind(this)
    }

    handleTab = (event, value) => {
      this.setState({value});
    };

    render() {

        const styles = {
            settingsCard: {
              margin: '0 auto',
              maxWidth: '50%'
            },
            username: {
              marginLeft: '1em'
            },
            grow: {
              flexGrow: 1
            },
            grow2: {
              flexGrow: 2
            },
            displayOption:{
              marginLeft: '1em',
              marginRight: '1em'
            }
          }

          //const { value } = this.state;

        return(
            <div>
              {/* Navigation Bar */}
                <AppBar position="static" >
                    <Toolbar className="green">
                        <Button  onClick={this.props.handleLogout}><Link to="/">LOG OUT</Link></Button>
                        <Button><Link to="/inner">My Job Tickets</Link></Button>                                 
                        <div style={styles.grow}/>             
                        <h1 className="green userview-logo">APPLi</h1>         
                    </Toolbar>
                </AppBar>
                {/* Settings Card */}
                <Card style={styles.settingsCard}>
                  <Tabs
                    value={this.state.value}
                    onChange={this.handleTab}
                    indicatorColor="primary"
                    textColor="primary"                    
                  >
                    <Tab label="User Style" onClick={this.handleTab} />
                    <Tab label="Reset Password" onClick={this.handleTab} />
                  </Tabs>
                  <CardContent>
                        {this.state.value === 0 && <UserStyle />}
                        {this.state.value === 1 && <ChangePassword />}
                  </CardContent>
                </Card>
            </div>
        )
    }

}

export default UserSettings
