import React, { Component } from 'react';
import NewTrackerDialog from './NewTrackerDialog.js';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";
import lightGreen from '@material-ui/core/colors/lightGreen';
import jwt_decode from 'jwt-decode';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';

import Button from '@material-ui/core/Button';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import axios from 'axios';



class UserView extends Component {

  constructor(props) {
    super(props)
    this.state = {
      open: false,

    }

    this.handleClickOpen = this.handleClickOpen.bind(this)
    this.handleClose = this.handleClose.bind(this)
  }

  handleClickOpen() {
    this.setState({open: true})
  }

  handleClose() {
    this.setState({open: false})
  }

  componentDidMount() {
    axios.get('/retrievetickets', {headers: { "Authorization": "Bearer " + localStorage.getItem('jwtToken') }}).then((user)=>{
      console.log('data coming', user);
      //dispatch(getUsersData(data));
    }).catch((error)=>{
      console.log('error ', error);
      this.props.history.push('/')
    });
  }


  render() {
    const primary = lightGreen.A400;

    //decode the jwt's payload.
    let decoded = jwt_decode(localStorage.getItem('jwtToken'))
    //get the current time
    let currentTime = Math.floor(Date.now() / 1000)
    //if the current time on rendering is earlier than the expiration date, show the page.
    if (currentTime < decoded.exp || localStorage.getItem('jwtToken') === false) { 
      return (
        <div className="App">
        <AppBar position="static">
          <Toolbar  color={primary}>
          <Button className="white-text" onClick={this.props.handleLogout}><Link to="/">LOG OUT</Link></Button>
          {/* <p>Hello . You are logged into the user view.</p>
          <p>Your user id is .</p> */}
          </Toolbar>
        </AppBar>         
        <p>This is where your tickets will go.</p>
        <Dialog open={this.state.open} onClose={this.handleClose}  aria-labelledby="form-dialog-title">
          <NewTrackerDialog currentUserId={'yes'} cancel={this.handleClose} />
        </Dialog>
        <Fab color="primary" aria-label="Add" onClick={this.handleClickOpen}>
          <AddIcon />
        </Fab>
       </div>
      );
    }
    //otherwise, send the user to the login page.
    else { 
      return (<Redirect to="/" />) 
    }

  }
}

export default UserView;
