import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Home from './Home/Home.js';
import UserView from './UserView/UserView.js'
import './App.css';

class App extends Component {

  constructor(props) {

    super(props)
    this.state = {
      authorized: false,
      currentUser: undefined
    }

    this.authorizeUser = this.authorizeUser.bind(this);
    //this.handleLogout = this.handleLogout.bind(this);
    this.rememberAuthorization = this.rememberAuthorization.bind(this);  
    this.setCurrentUser = this.setCurrentUser.bind(this)

  }



  rememberAuthorization() {

    // if the key exists in localStorage
    if (localStorage.hasOwnProperty("authorized")) {
      // get the key's value from localStorage
      let value = localStorage.getItem("authorized");

      // parse the localStorage string and setState
      try {
        value = JSON.parse(value);
        this.setState({ authorized: value });
      } catch (e) {
        // handle empty string
        this.setState({ authorized: value });
      }
    }

  }

  setCurrentUser(user) {
    this.setState({currentUser: user}, () => {

    }).then(console.log(`the current user is ${this.state.currentUser["username"]},
    and their user id is ${this.state.currentUser["user_id"]}`))
  }

  authorizeUser() {
      console.log(this.state.authorized)
      this.setState({ authorized: true, attemptMessage:'' }, () =>  {
        //console.log(this.state.authorized)
        localStorage.setItem("authorized", JSON.stringify(this.state.authorized))

      })
  }

  handleLogout() {
    this.setState({authorized: false}, () => {
      //console.log(this.state.authorized)
      localStorage.setItem("authorized", JSON.stringify(this.state.authorized))
      //console.log(this.state.authorized)
      //this.props.history.push("/");

    })
  }


  render() {
    return (

      <Router>
        <div className="App">
          <Route exact path="/" exact path='/' render={(props) => 
            <Home authorizeUser={this.authorizeUser} setCurrentUser={this.setCurrentUser} {...props} />
          }/>
          <Route exact path="/inner" component={UserView} />
       </div>
      </Router>
    );
  }
}

export default App;
