import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";
import Button from '@material-ui/core/Button';


class UserView extends Component {

  constructor(props) {
    super(props)
    this.state = {
    }
  }

  render() {

    let loggedIn = localStorage.getItem("authorized")
    let currentUser = localStorage.getItem("currentUser")
    let currentUserId = localStorage.getItem("currentUserId")
    if (loggedIn === "true") {
      return (
        <div className="App">
          <p>Hello {currentUser}. You are logged into the user view.</p>
          <p>Your user id is {currentUserId}.</p>
          <Button variant="contained" color="primary" className="white-text" onClick={this.props.handleLogout}><Link to="/">LOG OUT</Link></Button>
       </div>
      );
    }

    else { return (<Redirect to="/" />) }

  }
}

export default UserView;
