import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";

import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Switch from '@material-ui/core/Switch';
import Toolbar from '@material-ui/core/Toolbar';

class UserAppBar extends Component {

    constructor(props) {
        super(props)
        this.state= {
            anchorEl: null
        }

        this.sortMenuClose = this.sortMenuClose.bind(this)
        this.sortMenuOpen = this.sortMenuOpen.bind(this)
    }

    sortMenuClose(sortOrder) {

        this.props.changeSortOrder(sortOrder).then

        this.setState({anchorEl: null})

      }

      sortMenuOpen = (event) => {
        this.setState({anchorEl: event.currentTarget})
      }

    render() {
        const { anchorEl } = this.state;

        const styles = {

            username: {
            marginLeft: '1em'
            },
            grow: {
            flexGrow: 1
            },
            grow2: {
            flexGrow: 2
            },
            displayOption:{
            marginLeft: '1em',
            marginRight: '1em'
            }
        }

        return (
            <div>
                <AppBar position="static" >
                    <Toolbar  className="green">
                        <Button  onClick={this.props.handleLogout}><Link to="/">LOG OUT</Link></Button>
                        <p style={styles.username}>Hello {this.props.currentUsername}</p>
                        <div style={styles.grow}/>     
                        <FormControlLabel control={
                        <Switch checked={this.props.showArchived} onChange={this.props.handleSwitch('showArchived')} value="showArchived" />                   
                        }
                        labelPlacement="start"
                        label="Show Archived Tickets"
                        style={styles.displayOption}
                        />

                        <Button
                        style={styles.displayOption}

                        aria-owns={anchorEl ? "sort-form" : null}
                        aria-haspopup="true"
                        onClick={this.props.sortMenuOpen}
                        >
                        Sort By
                        </Button>
                        <Menu 
                        id="sort-form" 
                        anchorEl={anchorEl}
                        open={Boolean(anchorEl)}
                        onClose={this.props.sortMenuClose}
                        >
                        <MenuItem >Sort By</MenuItem>
                        <MenuItem onClick={() => this.props.sortMenuClose("newToOld")}>Newest to Oldest</MenuItem>
                        <MenuItem onClick={() => this.props.sortMenuClose("oldToNew")}>Oldest to Newest</MenuItem>                               
                        </Menu>             
                        <div style={styles.grow}/>             
                        <h1 className="green userview-logo">APPLi</h1>         
                    </Toolbar>
                </AppBar>
            </div>
        )
    }
}

export default UserAppBar
