import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Home from './Home/Home.js';
import UserView from './UserView/UserView.js'
import './App.css';

class App extends Component {

  constructor(props) {
    super(props)
    this.state = {
      authorized: false
    }

    this.handleLogin = this.handleLogin.bind(this);
    //this.handleLogout = this.handleLogout.bind(this);
    //this.rememberAuthorization = this.rememberAuthorization.bind(this);  }

  }

  authorizeUser() {
    if (username === "admin" && password === "dapassword") {
      //console.log(this.state.authorized)
      this.setState({ authorized: true, attemptMessage:'' }, () => {
        //console.log(this.state.authorized)
        localStorage.setItem("authorized", JSON.stringify(this.state.authorized))
        //close()

      })

    }
    else {
      this.setState({attemptMessage: 'ACCESS DENIED: User not recognized'})
      //console.log("access denied")
    }

  }
  render() {
    return (

      <Router>
        <div className="App">
          <Route exact path="/" handleLogin={this.handleLogin} component={Home} />
          <Route exact path="/inner" component={UserView} />
       </div>
      </Router>
    );
  }
}

export default App;
