import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Home from './Home/Home.js';
import UserView from './UserView/UserView.js'
import './App.css';

class App extends Component {

  constructor(props) {
    super(props)
    this.state = {
      authorized: false
    }

    this.authorizeUser = this.handleLogin.bind(this);
    //this.handleLogout = this.handleLogout.bind(this);
    this.rememberAuthorization = this.rememberAuthorization.bind(this);  

  }

  rememberAuthorization() {

    // if the key exists in localStorage
    if (localStorage.hasOwnProperty("authorized")) {
      // get the key's value from localStorage
      let value = localStorage.getItem("authorized");

      // parse the localStorage string and setState
      try {
        value = JSON.parse(value);
        this.setState({ authorized: value });
      } catch (e) {
        // handle empty string
        this.setState({ authorized: value });
      }
    }

}

  authorizeUser() {
      console.log(this.state.authorized)
      this.setState({ authorized: true, attemptMessage:'' }, () =>  {
        //console.log(this.state.authorized)
        localStorage.setItem("authorized", JSON.stringify(this.state.authorized))
        //close()

      })
  }


  render() {
    return (

      <Router>
        <div className="App">
          <Route exact path="/" authorizeUser={this.handleLogin} component={Home} />
          <Route exact path="/inner" component={UserView} />
       </div>
      </Router>
    );
  }
}

export default App;
