import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";
import lightGreen from '@material-ui/core/colors/lightGreen';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';

import Button from '@material-ui/core/Button';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import DeleteIcon from '@material-ui/icons/Delete';
import NavigationIcon from '@material-ui/icons/Navigation';


class UserView extends Component {

  constructor(props) {
    super(props)
    this.state = {
    }
  }

  render() {

    let loggedIn = localStorage.getItem("authorized")
    let currentUser = localStorage.getItem("currentUser")
    let currentUserId = localStorage.getItem("currentUserId")
    if (loggedIn === "true") {
      return (
        <div className="App">
        <AppBar position="static">
          <Toolbar>
          <Button color="primary" className="white-text" onClick={this.props.handleLogout}><Link to="/">LOG OUT</Link></Button>

          <p>Hello {currentUser}. You are logged into the user view.</p>
          <p>Your user id is {currentUserId}.</p>
          </Toolbar>
        </AppBar>         
        <p>This is where your tickets will go.</p>
        <Fab color="primary" aria-label="Add">
          <AddIcon />
        </Fab>
       </div>
      );
    }

    else { 
      return (<Redirect to="/" />) }

  }
}

export default UserView;
