import { createMuiTheme } from '@material-ui/core/styles';
import red from '@material-ui/core/colors/red';


const muiTheme = createMuiTheme({
    palette: {
        primary: { 
            main: '#36c136',
            contrastText: '#fff', 
        }, // custom color in hex
        secondary: { main: '#4286f4' },
        overrides: {
            MuiCard: {
              root: {
                fontWeight: "bold",
                backgroundColor: "red",
                margin: "10px",
                "&:hover": {
                  backgroundColor: "green"
                }
              }
            }
            }            

    }
});

export default muiTheme
