import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";
import lightGreen from '@material-ui/core/colors/lightGreen';

import TextField from '@material-ui/core/TextField';

import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';

import Grid from '@material-ui/core/Grid';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormControl from '@material-ui/core/FormControl';


import Button from '@material-ui/core/Button';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import DeleteIcon from '@material-ui/icons/Delete';
import NavigationIcon from '@material-ui/icons/Navigation';

class NewTrackerDialog extends Component {

    constructor(props) {
      super(props)
      this.state = {
        companyName: '',
        positionTitle: '',
        resumeLink: ''
      }
    }

    handleChange = prop => event => {
      this.setState({ [prop]: event.target.value });
    };

    render() {

        return (
          <div className="App" style={{ padding: 12 }}>
              <DialogTitle id="form-dialog-title">Create an Application Tracker</DialogTitle>
              <form>

              <Grid container spacing={24}>
                <Grid item xs={6}>
                  <TextField
                    required
                    label="Company"
                    value={this.state.companyName}
                    placeholder="The Company Name"
                    onChange={this.handleChange('companyName')}
                    margin="normal"
                    variant="filled"
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    required
                    label="Company"
                    value={this.state.positionTitle}
                    placeholder="The Company Name"
                    onChange={this.handleChange('positionTitle')}
                    margin="normal"
                    variant="filled"
                  />                    
                </Grid>
                <Grid item xs={12}>
                <TextField
                    required
                    fullWidth
                    label="Resume"
                    value={this.state.positionTitle}
                    placeholder="A Link to Your Resume"
                    onChange={this.handleChange('positionTitle')}
                    margin="normal"
                    variant="filled"
                  />     
                </Grid>
              </Grid>


              </form>
         </div>
        );

    }
  }

  export default NewTrackerDialog;
